function t(t){return t*Math.PI/180}function n(n,e){let a=t(n[1]),r=t(e[1]),h=(r-a)/2,M=t(e[0]-n[0])/2,i=Math.sin(h)*Math.sin(h)+Math.sin(M)*Math.sin(M)*Math.cos(a)*Math.cos(r);return 12756274*Math.atan2(Math.sqrt(i),Math.sqrt(1-i))}export function getLength(t){let e=0;for(let a=1;a<t.length;a++)e+=n(t[a-1],t[a]);return e}export function getArea(n){return Math.abs(function(n){let e=0,a=n.length,r=n[a-1][0],h=n[a-1][1];for(let M=0;M<a;M++){let a=n[M][0],i=n[M][1];e+=t(a-r)*(2+Math.sin(t(h))+Math.sin(t(i))),r=a,h=i}return 6378137*e*6378137/2}(n))}