function t(t){return t*Math.PI/180}function n(n,e){const a=t(n[1]),r=t(e[1]),h=(r-a)/2,o=t(e[0]-n[0])/2,s=Math.sin(h)*Math.sin(h)+Math.sin(o)*Math.sin(o)*Math.cos(a)*Math.cos(r);return 12756274*Math.atan2(Math.sqrt(s),Math.sqrt(1-s))}export function getLength(t){let e=0;for(let a=1;a<t.length;a++)e+=n(t[a-1],t[a]);return e}export function getArea(n){return Math.abs(function(n){let e=0;const a=n.length;let r=n[a-1][0],h=n[a-1][1];for(let o=0;o<a;o++){const a=n[o][0],s=n[o][1];e+=t(a-r)*(2+Math.sin(t(h))+Math.sin(t(s))),r=a,h=s}return 6378137*e*6378137/2}(n))}