import{ol}from"../../deps.js";export default function(options){switch(options.type){case"WMS":return options.sourceOptions.tileGrid=new ol.tilegrid.TileGrid(options.tileGridOptions),[new ol.layer.Tile({id:options.id,source:new ol.source.TileWMS(options.sourceOptions)})];case"WMTS":return options.sourceOptions.tileGrid=new ol.tilegrid.WMTS(options.tileGridOptions),[new ol.layer.Tile({id:options.id,extent:options.extent,source:new ol.source.WMTS(options.sourceOptions)})];case"XYZ":return options.tileGridOptions&&(options.sourceOptions.tileGrid=new ol.tilegrid.TileGrid(options.tileGridOptions)),[new ol.layer.Tile({id:options.id,source:new ol.source.XYZ(options.sourceOptions)})];default:console.log("Only caters for WMS/WMTS/XYZ")}}